def tageszeit():
    '''
        get time of day
        0 - night
        1 - dusk/dawn
        2 - day

        @return int
    '''
    # TODO: make this a parameter?
    hours = time.strftime('%H')

    if hours < 4:
        return 0
    elif hours < 8:
        return 1
    elif hours < 18:
        return 2
    elif hours < 21:
        return 1
    else:
        return 0


def sleep_time():
    '''
        get charakters sleeping time

        @return bool True= Time to sleep
    '''
    # TODO: make this a parameter?
    hours = time.strftime('%H')

    if C['TIMEOFDAY'] >= 0:
        return C['TIMEOFDAY']
    elif hours < 7:
        return True
    elif hours < 23:
        return False
    else:
        return True


def is_today(req_time):
    '''
        checks if timestamp is today
    '''
    req_date = datetime.fromtimestamp(req_time/1000)
    return req_date.date() == datetime.today().date()


def time_str(req_time, short=False):
    '''
        convert datetime object to formatted string

    '''

    req_date = datetime.fromtimestamp(req_time/1000)

    formats = {
        "de": {
            'short': '%d.%m. %H:%M',
            'long': '%d.%m.%Y %H:%M:%S'
        },
        "pl": {
            'short': '%d/%m/ %H:%M',
            'long': '%d/%m/%Y %H:%M:%S'
        },
        "default": {
            'short': '%d/%m/ %H:%M',
            'long': '%d/%m/%Y %H:%M:%S'
        }
    }

    code = lang_code
    if code not in formats.keys():
        code = "default"

    length = 'long'
    if short:
        length = 'short'

    return req_date.strftime(formats[code][length])


