def process_arg(arg):
    '''
        process popup argument
    '''
    i_array = 0
    tmp_do = None
    if arg is list:
        for i_array in arg:
            process_arg(i_array)

    elif arg is int:
        if arg < 0:
            popup_width = -arg
        elif arg == 0:
            textX = 0
            if text_dir == "right":
                textX = popup_width

            textY += last_text_height + 10
        elif text_dir == "right":
            textX = popup_width - arg
        else:
            textX = arg

    elif arg is TextFormat:
        tmp_text_format = arg

    elif arg is DisplayObject:
        tmp_do = Bitmap(arg.content.bitmapData.clone())
        with tmp_do:
            if text_dir == "right":
                if textX < popup_width:
                    x = textX - width
                    textX = textX - width + 5
                    y = textY
                else:
                    x = popup_width - 5 - width
                    y = textY
                    textY = textY + textHeight + 10
            else:
                if textX > 0:
                    x = textX
                    textX = textX + width + 5
                    y = textY
                else:
                    x = 5
                    y = textY
                    textY = textY + textHeight + 10
        actor[POPUP_INFO].addChild(tmp_do)

    elif arg is str:
        arg = arg.replace("#", chr(13))
        tmp_text_field = TextField()
        with tmp_text_field:
            auto_size = TextFieldAutoSize.LEFT
            background = False
            selectable = False
            embed_fonts = font_embedded
            default_text_format = tmp_text_format
            htmlText = arg
            last_text_height = textHeight
            if text_dir == "right":
                auto_size = TextFieldAutoSize.RIGHT
                if textX < popup_width:
                    x = textX - text_width
                    textX -= text_width + 5
                    y = textY
                else:
                    x = popup_width - 5 - text_width
                    y = textY
                    textY += textHeight + 10
            else:
                if textX > 0:
                    x = textX
                    textX += text_width + 5
                    y = textY
                else:
                    x = 5
                    y = textY
                    textY += textHeight + 10

                if (x + text_width + 10) > popup_width:
                    popup_width = x + text_width + 10

            actor[POPUP_INFO].addChild(tmp_text_field)


